import React from "react";
import { Button, Card } from "antd";
import { ExclamationCircleOutlined } from "@ant-design/icons";
import { useNavigate } from "react-router-dom";
import { useState } from "react";
import { ethers } from "ethers";

function CreateAccount() {
  const [newSeedPhrase, setNewSeedPhrase] = useState(null);
  const navigate = useNavigate();
  function generateWallet(){
    const mnemonic = ethers.Wallet.createRandom().mnemonic.phrase;
    setNewSeedPhrase(mnemonic)
  }
  function setWalletAndMnemonic(){
    setSeedPhrase(newSeedPhrase);
    setWallet(ethers.Wallet.fromPhrase(newSeedPhrase).address)
  }

  return (     //* "ExclamationCircleOutlined" in this part when click the create button its getting urgent about the seed phrase. and also "mnemonic" from comes in css file
    <>
      <div className="content">
      <div className="mnemonic"> 
          <ExclamationCircleOutlined style={{ fontSize: "10px" }} />   
          <div>
          Once you generate the seed phrase, save it securely in order to
            recover your wallet in the future.
          </div>
          </div>
          <Button   //! generate "Seed Phrase"
          className="frontPageButton"
          type="primary"
          onClick={() => generateWallet()}
        >
          Generate Seed Phrase
        </Button>

        <Card className="seedPhraseContainer"> 

         {newSeedPhrase && <pre style={{whiteSpace: "pre-wrap"}}>{newSeedPhrase}</pre>} 
        </Card> 

        <Button
          className="frontPageButton"
          type="default"
          onClick={() => setWalletAndMnemonic()}
        >
          Open Your New Wallet
        </Button>
        <p className="frontPageBottom" onClick={()=>navigate("/")}>
          Back Home
        </p>
      </div>
    </>
  );
}

export default CreateAccount;
